mapper.select:

select rnum, mid, nno, ntitle, ncontent, ndate
from(
	select rownum as rnum, mid, nno, ntitle, ncontent, ndate
	from(
		select mid, nno, ntitle, ncontent, ndate
		from notice
		order by ndate desc	
	)
	where rownum <= #{endRowNo}
)
where rnum >= #{startRowNo}

select mid, nno, ntitle, ncontent, ndate
from notice
where nno=#{nno}
